mapper.select:
SELECT  FROM txs_customer

    
        AND id = #{id}
    
    
        AND id IN #{id}
    
    
        AND `fullName`like CONCAT('%', #{fullName}, '%')
    
    
        AND `sex` = #{sex}
    
    
        AND `phoneNumber`like CONCAT('%', #{phoneNumber}, '%')
    
    
        AND `remark`like CONCAT('%', #{remark}, '%')
    
    
    
        AND date_format(createTime,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')
    
    
    
        AND date_format(createTime,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')

mapper.update:
UPDATE txs_customer
fullName = #{fullName}, sex = #{sex}, phoneNumber = #{phoneNumber}, remark = #{remark}, updateBy = #{updateBy}, updateTime = #{updateTime},
WHERE id = #{id}

mapper.insert:
INSERT INTO txs_customer(
    fullName, sex, phoneNumber, remark, createBy, createTime, updateBy, updateTime
)values(
    #{fullName}, #{sex}, #{phoneNumber}, #{remark}, #{createBy}, #{createTime}, #{updateBy}, #{updateTime}
)
