mapper.select:
SELECT  FROM txs_order

    
        AND id = #{id}
    
    
        AND customerId = #{customerId}
    
    
        AND `payeeName`like CONCAT('%', #{payeeName}, '%')
    
    
        AND setMealId = #{setMealId}
    
    
        AND `orderNo`like CONCAT('%', #{orderNo}, '%')
    
    
    
        AND date_format(payTime,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')
    
    
    
        AND date_format(payTime,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')
