mapper.select:
SELECT o.* FROM txs_order AS o

    RIGHT JOIN txs_customer AS c on o.customerId = c.id


    
        AND o.id = #{id}
    
    
        AND o.customerId = #{customerId}
    
    
        AND o.`payeeName`like CONCAT('%', #{payeeName}, '%')
    
    
        AND o.setMealId = #{setMealId}
    
    
        AND o.`orderNo`like CONCAT('%', #{orderNo}, '%')
    
    
        AND c.`fullName`like CONCAT('%', #{customerName}, '%')
    
    
        AND c.`phoneNumber`like CONCAT('%', #{customerPhone}, '%')
    
    
        AND o.setMealId IN
        #{id}
    
    
    
        AND date_format(o.payTime,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')
    
    
    
        AND date_format(o.payTime,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')

mapper.update:
UPDATE txs_order
customerId = #{customerId}, payTime = #{payTime}, payeeName = #{payeeName}, payType = #{payType}, setMealId = #{setMealId}, laterConsumption = #{laterConsumption}, choosePhoto = #{choosePhoto}, photographTime = #{photographTime}, payAmount = #{payAmount}, receiveFinishedProduct = #{receiveFinishedProduct}, remark = #{remark}, updateBy = #{updateBy}, updateTime = #{updateTime},
WHERE id = #{id}
