mapper.select:
SELECT  FROM txs_order

    
        AND id = #{id}
    
    
        AND customerId = #{customerId}
    
    
        AND payeeId = #{payeeId}
    
    
        AND setMealId = #{setMealId}
    
    
        AND payType = #{payType}
    
    
        AND `orderNo`like CONCAT('%', #{orderNo}, '%')
    
    
    
        AND date_format(createTime,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')
    
    
    
        AND date_format(createTime,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')

mapper.update:
UPDATE txs_order
name = #{name}, days = #{days}, remark = #{remark}, updateBy = #{updateBy}, updateTime = #{updateTime},
WHERE id = #{id}
